import React, { Component } from "react";
import { Post } from "./Post/Post";

class App extends Component {
  // Состояния для данных, загрузки и ошибок
  state = {
    posts: [],
    loading: true,
    error: null,
  };

  // Метод жизненного цикла componentDidMount() для выполнения запроса
  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json()) // Преобразуем ответ в JSON
      .then((data) => {
        this.setState({
          posts: data, // Сохраняем данные в состоянии
          loading: false, // Заканчиваем загрузку
        });
      })
      .catch((err) => {
        this.setState({
          error: err, // Сохраняем ошибку в состоянии
          loading: false, // Заканчиваем загрузку
        });
      });
  }

  render() {
    const { posts, loading, error } = this.state;

    // Если идет загрузка, показываем индикатор загрузки
    if (loading) {
      return <div>Загрузка...</div>;
    }

    // Если произошла ошибка, показываем ошибку
    if (error) {
      return <div>Произошла ошибка: {error.message}</div>;
    }

    return (
      <div>
        <h1>Список постов</h1>
        <div className="post-list">
          {posts.map((post) => (
            <Post key={post.id} post={post} />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
