import axios from "axios";

axios.defaults.baseURL = "https://67c027cfb9d02a9f22488a0b.mockapi.io";

export const addMaterial = async (values) => {
  console.log("Отправляем запрос через fetch...");

  try {
    const response = await fetch(
      "https://67c027cfb9d02a9f22488a0b.mockapi.io/material",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(values),
      }
    );

    const data = await response.json();
    console.log("Ответ от сервера через fetch:", data);
    return data;
  } catch (error) {
    console.error("Ошибка fetch запроса:", error);
  }
};
