import React, { useState, useEffect, useCallback } from "react";
import { createPortal } from "react-dom";

const API_KEY = "your_key";
const BASE_URL = "https://pixabay.com/api/";

const Searchbar = ({ onSubmit }) => {
  const [query, setQuery] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (query.trim() === "") return;
    onSubmit(query);
    setQuery("");
  };

  return (
    <header className="searchbar">
      <form className="form" onSubmit={handleSubmit}>
        <button type="submit" className="button">
          <span className="button-label">Поиск</span>
        </button>
        <input
          className="input"
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          autoComplete="off"
          autoFocus
          placeholder="Поиск изображений и фотографий"
        />
      </form>
    </header>
  );
};

const ImageGallery = ({ images, onImageClick }) => (
  <ul className="gallery">
    {images.map((image) => (
      <ImageGalleryItem key={image.id} image={image} onClick={onImageClick} />
    ))}
  </ul>
);

const ImageGalleryItem = ({ image, onClick }) => (
  <li className="gallery-item" onClick={() => onClick(image.largeImageURL)}>
    <img src={image.webformatURL} alt="" />
  </li>
);

const Button = ({ onClick }) => (
  <button className="button-load" onClick={onClick}>
    Загрузить больше
  </button>
);

const Loader = () => (
  <div className="loader">
    <div className="spinner"></div>
  </div>
);

// Стили для анимации
const loaderStyles = `
  .loader {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
  }
  .spinner {
    border: 4px solid #f3f3f3; /* Light grey */
    border-top: 4px solid #3498db; /* Blue */
    border-radius: 50%;
    width: 50px;
    height: 50px;
    animation: spin 2s linear infinite;
  }
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const Modal = ({ largeImage, onClose }) => {
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "Escape") onClose();
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [onClose]);

  return createPortal(
    <div className="overlay" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <img src={largeImage} alt="" />
      </div>
    </div>,
    document.body
  );
};

function App() {
  const [images, setImages] = useState([]);
  const [query, setQuery] = useState("");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [largeImage, setLargeImage] = useState(null);
  const [error, setError] = useState(null); // Добавлено состояние ошибки

  useEffect(() => {
    if (!query) return;

    const fetchImages = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          `${BASE_URL}?q=${query}&page=${page}&key=${API_KEY}&image_type=photo&orientation=horizontal&per_page=12`
        );
        const data = await response.json();
        if (data.hits) {
          setImages((prev) =>
            page === 1 ? data.hits : [...prev, ...data.hits]
          );
        } else {
          setError("Изображения не найдены");
        }
      } catch (error) {
        setError("Что-то пошло не так. Попробуйте снова позже.");
      } finally {
        setLoading(false);
      }
    };

    fetchImages();
  }, [query, page]);

  const handleSearch = (newQuery) => {
    setQuery(newQuery);
    setPage(1);
    setImages([]);
    setError(null); // Сбрасываем состояние ошибки при новом поиске
  };

  const handleImageClick = useCallback((imageUrl) => {
    setLargeImage(imageUrl);
  }, []);

  return (
    <div className="App">
      <Searchbar onSubmit={handleSearch} />
      {error && <p className="error">{error}</p>}{" "}
      {/* Показываем сообщение об ошибке */}
      <ImageGallery images={images} onImageClick={handleImageClick} />
      {loading && <Loader />}
      {images.length > 0 && !loading && (
        <Button onClick={() => setPage((prev) => prev + 1)} />
      )}
      {largeImage && (
        <Modal largeImage={largeImage} onClose={() => setLargeImage(null)} />
      )}
      <style>{loaderStyles}</style> {/* Стили для загрузки */}
    </div>
  );
}

export default App;
