import React, { Component } from "react";

class App extends Component {
  state = {
    post: [],
    loading: true,
    error: null,
  };

  componentDidMount() {
    fetch("http://jsonplaceholder.typecode.com/post").then((response) =>
      response
        .json()
        .then((data) => {
          this.setState({ post: data, loading: false });
        })
        .catch((error) => {
          this.setState({ error: error, loading: false });
        })
    );
  }
  render() {
    const { posts, loading, error } = this.state;

    if (loading) {
      return <div>Загрузка...</div>;
    }

    if (error) {
      return <div>Произошла ошибка: {error.message}</div>;
    }

    return (
      <div>
        <h2>Список постов</h2>
        <div>
          {posts.map((post) => (
            <div key={post.id}>
              <h3>{post.title}</h3>
              <p>{post.body}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
