import React, { Component } from "react";
import { createPortal } from "react-dom";

const API_KEY = "your_key";
const BASE_URL = "https://pixabay.com/api/";

class Searchbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      query: "",
    };
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const { query } = this.state;
    if (query.trim() === "") return;
    this.props.onSubmit(query);
    this.setState({ query: "" });
  };

  handleChange = (e) => {
    this.setState({ query: e.target.value });
  };

  render() {
    const { query } = this.state;
    return (
      <header className="searchbar">
        <form className="form" onSubmit={this.handleSubmit}>
          <button type="submit" className="button">
            <span className="button-label">Поиск</span>
          </button>
          <input
            className="input"
            type="text"
            value={query}
            onChange={this.handleChange}
            autoComplete="off"
            autoFocus
            placeholder="Поиск изображений и фотографий"
          />
        </form>
      </header>
    );
  }
}

class ImageGalleryItem extends Component {
  handleClick = () => {
    const { image, onClick } = this.props;
    onClick(image.largeImageURL);
  };

  render() {
    const { image } = this.props;
    return (
      <li className="gallery-item" onClick={this.handleClick}>
        <img src={image.webformatURL} alt="" />
      </li>
    );
  }
}

class ImageGallery extends Component {
  render() {
    const { images, onImageClick } = this.props;
    return (
      <ul className="gallery">
        {images.map((image) => (
          <ImageGalleryItem
            key={image.id}
            image={image}
            onClick={onImageClick}
          />
        ))}
      </ul>
    );
  }
}

class Button extends Component {
  render() {
    const { onClick } = this.props;
    return (
      <button className="button-load" onClick={onClick}>
        Загрузить больше
      </button>
    );
  }
}

class Loader extends Component {
  render() {
    return (
      <div className="loader">
        <div className="spinner"></div>
      </div>
    );
  }
}

class Modal extends Component {
  componentDidMount() {
    window.addEventListener("keydown", this.handleKeyDown);
  }

  componentWillUnmount() {
    window.removeEventListener("keydown", this.handleKeyDown);
  }

  handleKeyDown = (e) => {
    if (e.key === "Escape") this.props.onClose();
  };

  render() {
    const { largeImage, onClose } = this.props;
    return createPortal(
      <div className="overlay" onClick={onClose}>
        <div className="modal" onClick={(e) => e.stopPropagation()}>
          <img src={largeImage} alt="" />
        </div>
      </div>,
      document.body
    );
  }
}

class App extends Component {
  state = {
    images: [],
    query: "",
    page: 1,
    loading: false,
    largeImage: null,
    error: null,
  };

  componentDidUpdate(prevProps, prevState) {
    const { query, page } = this.state;
    if (prevState.query !== query || prevState.page !== page) {
      this.fetchImages();
    }
  }

  fetchImages = async () => {
    const { query, page } = this.state;
    if (!query) return;

    this.setState({ loading: true });

    try {
      const response = await fetch(
        `${BASE_URL}?q=${query}&page=${page}&key=${API_KEY}&image_type=photo&orientation=horizontal&per_page=12`
      );
      const data = await response.json();
      if (data.hits) {
        this.setState((prevState) => ({
          images: page === 1 ? data.hits : [...prevState.images, ...data.hits],
        }));
      } else {
        this.setState({ error: "Изображения не найдены" });
      }
    } catch (error) {
      this.setState({ error: "Что-то пошло не так. Попробуйте снова позже." });
    } finally {
      this.setState({ loading: false });
    }
  };

  handleSearch = (newQuery) => {
    this.setState({
      query: newQuery,
      page: 1,
      images: [],
      error: null,
    });
  };

  handleImageClick = (imageUrl) => {
    this.setState({ largeImage: imageUrl });
  };

  handleLoadMore = () => {
    this.setState((prevState) => ({ page: prevState.page + 1 }));
  };

  handleCloseModal = () => {
    this.setState({ largeImage: null });
  };

  render() {
    const { images, loading, error, largeImage } = this.state;
    return (
      <div className="App">
        <Searchbar onSubmit={this.handleSearch} />
        {error && <p className="error">{error}</p>}
        <ImageGallery images={images} onImageClick={this.handleImageClick} />
        {loading && <Loader />}
        {images.length > 0 && !loading && (
          <Button onClick={this.handleLoadMore} />
        )}
        {largeImage && (
          <Modal largeImage={largeImage} onClose={this.handleCloseModal} />
        )}
      </div>
    );
  }
}

export default App;
