import axios from "axios";

axios.defaults.baseURL = "https://67c027cfb9d02a9f22488a0b.mockapi.io";

export const addMaterial = async (values) => {
  console.log("API.addMaterial: отправляем запрос с данными:", values); // ✅ Данные перед запросом

  try {
    const response = await axios.post("/material", values);
    console.log("API.addMaterial: сервер ответил:", response.data); // ✅ Ответ сервера
    return response.data;
  } catch (error) {
    console.error("API.addMaterial: ошибка запроса!", error); // ❌ Ошибка, если запрос не удался
  }
};
