import React, { Component } from "react";

class App extends Component {
  state = {
    postData: null, // Состояние для хранения данных, которые мы отправим
    loading: false, // Состояние загрузки
    error: null, // Состояние ошибки
  };

  // Метод для отправки POST-запроса
  sendData = async () => {
    this.setState({ loading: true }); // Устанавливаем статус загрузки

    const newPost = {
      title: "My New Post",
      body: "This is the content of the new post.",
      userId: 1,
    };

    try {
      const response = await fetch(
        "https://jsonplaceholder.typicode.com/posts",
        {
          method: "POST", // Указываем метод POST
          headers: {
            "Content-Type": "application/json", // Отправляем данные в формате JSON
          },
          body: JSON.stringify(newPost), // Данные, которые мы отправляем
        }
      );

      if (!response.ok) {
        throw new Error("Ошибка при отправке данных");
      }

      const data = await response.json(); // Получаем данные ответа
      this.setState({
        postData: data, // Сохраняем ответ сервера в состоянии
        loading: false, // Заканчиваем загрузку
      });
    } catch (error) {
      this.setState({
        error: error.message, // Если ошибка, сохраняем ее в состоянии
        loading: false, // Заканчиваем загрузку
      });
    }
  };

  render() {
    const { postData, loading, error } = this.state;

    return (
      <div>
        <h1>Отправка POST запроса</h1>
        <button onClick={this.sendData}>Отправить данные</button>
        {loading && <div>Загрузка...</div>}{" "}
        {/* Показываем, если идет загрузка */}
        {error && <div>Ошибка: {error}</div>}{" "}
        {/* Показываем ошибку, если она возникла */}
        {postData && (
          <div>
            <h3>Ответ от сервера:</h3>
            <pre>{JSON.stringify(postData, null, 2)}</pre>{" "}
            {/* Показываем ответ от сервера */}
          </div>
        )}
      </div>
    );
  }
}

export default App;
